import React, { useEffect, useState } from "react";
import { WagmiConfig, createConfig, useAccount, useConnect, useDisconnect } from "wagmi";
import { injected } from "@wagmi/connectors";
import { ethers } from "ethers";

const CONTRACT_ADDRESS = "0xAeE47FF0114fB8A20cF93eB5CBc2b9663b5fF75a";
const CONTRACT_ABI = [
  "function connect() public",
  "function totalVisitors() public view returns (uint256)"
];

// Wagmi config with injected connector
const config = createConfig({
  autoConnect: true,
  connectors: [injected()],
});

function AppContent() {
  const { address, isConnected } = useAccount();
  const { connect } = useConnect();
  const { disconnect } = useDisconnect();

  const [visitorCount, setVisitorCount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [txPending, setTxPending] = useState(false);

  // Get contract instance with signer or provider
  function getContract(signerOrProvider) {
    return new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signerOrProvider);
  }

  // Fetch visitor count (read-only)
  async function fetchVisitorCount() {
    if (!window.ethereum) {
      alert("Please install MetaMask or compatible wallet");
      return;
    }
    setLoading(true);
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = getContract(provider);
      const count = await contract.totalVisitors();
      setVisitorCount(Number(count));
    } catch (error) {
      console.error("Failed to fetch visitor count", error);
    } finally {
      setLoading(false);
    }
  }

  // Register visit (send transaction)
  async function registerVisit() {
    if (!window.ethereum) {
      alert("Please install MetaMask or compatible wallet");
      return;
    }
    setTxPending(true);
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = getContract(signer);
      const tx = await contract.connect();
      await tx.wait();
      alert("Visit registered successfully!");
      fetchVisitorCount();
    } catch (error) {
      console.error(error);
      alert("Transaction failed or rejected.");
    } finally {
      setTxPending(false);
    }
  }

  useEffect(() => {
    if (isConnected) {
      fetchVisitorCount();
    } else {
      setVisitorCount(null);
    }
  }, [isConnected]);

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", padding: "1rem", fontFamily: "Arial, sans-serif" }}>
      <h1>Visitor Wallet Counter</h1>

      {!isConnected ? (
        <button onClick={() => connect()}>Connect Wallet</button>
      ) : (
        <>
          <p><strong>Connected wallet:</strong> {address}</p>
          <button onClick={registerVisit} disabled={txPending}>
            {txPending ? "Registering visit..." : "Register Visit"}
          </button>
          <button onClick={() => disconnect()} style={{ marginLeft: "1rem" }}>Disconnect</button>
        </>
      )}

      <div style={{ marginTop: "1.5rem" }}>
        {loading ? (
          <p>Loading visitor count...</p>
        ) : (
          visitorCount !== null && <p><strong>Total unique visitors:</strong> {visitorCount}</p>
        )}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <WagmiConfig config={config}>
      <AppContent />
    </WagmiConfig>
  );
}
