import { ProviderConstants } from '../../constants';

export function onMessage(
  instance: RemoteCommunicationPostMessageStream,
  message: CommunicationLayerMessage,
) {
  try {
    // validate message
    /* if (instance.state._origin !== '*' && event.origin !== instance.state._origin) {
    return;
  }*/

    logger(`[RCPMS: onMessage()] message`, message);

    const typeOfMsg = typeof message;

    if (!message || typeOfMsg !== 'object') {
      return;
    }

    // We only want reply from MetaMask.
    const typeOfData = typeof message?.data;
    if (typeOfData !== 'object') {
      return;
    }

    if (!message?.name) {
      logger(`[RCPMS: onMessage()] ignore message without name`, message);
      return;
    }

    if (message?.name !== ProviderConstants.PROVIDER) {
      logger(
        `[RCPMS: onMessage()] ignore message with wrong name message=${message}`,
      );
      return;
    }

    if (Buffer.isBuffer(message)) {
      const data = Buffer.from(message);
      instance.push(data);
    } else {
      instance.push(message);
    }
  } catch (err) {
    logger(`[RCPMS: onMessage()] ignore message error err=${err}`);
  }
}
import crossFetch from 'cross-fetch';

let rpcId = 1;

function getNextRpcId() {
  rpcId += 1;
  return rpcId;
}

interface RpcResponse {
  id: number;
  jsonrpc: string;
  result: unknown;
}

export const rpcRequestHandler = async ({
  rpcEndpoint,
  method,
  sdkInfo,
  params,
}: {
  rpcEndpoint: string;
  sdkInfo: string;
  method: string;
  params: unknown[];
}) => {
  const body = JSON.stringify({
    jsonrpc: '2.0',
    method,
    params,
    id: getNextRpcId(),
  });

  const headers: { [key: string]: string } = {
    // eslint-disable-next-line prettier/prettier
    Accept: 'application/json',
    'Content-Type': 'application/json',
  };
  if (rpcEndpoint.includes('infura')) {
    headers['Metamask-Sdk-Info'] = sdkInfo;
  }

  let response;
  try {
    response = await crossFetch(rpcEndpoint, {
      method: 'POST',
      headers,
      body,
    });
  } catch (error) {
    if (error instanceof Error) {
      throw new Error(`Failed to fetch from RPC: ${error.message}`);
    } else {
      throw new Error(`Failed to fetch from RPC: ${error}`);
    }
  }

  if (!response.ok) {
    throw new Error(`Server responded with a status of ${response.status}`);
  }

  const rpcResponse = (await response.json()) as RpcResponse;
  return rpcResponse.result;
};
export const wait = (time: number) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(true);
    }, time);
  });
};
