export const handleBatchMethod = async ({
  target,
  args,
  trackEvent,
  sdkInstance,
}: {
  target: MetaMaskInpageProvider;
  args: RequestArguments;
  trackEvent: boolean;
  sdkInstance: MetaMaskSDK;
