  }) => void;
}

export interface PendingWidgetProps extends Components.MmPendingModal {
  parentElement: Element;
  onClose: () => void;
  onDisconnect?: () => void;
  updateOTPValue: (otpValue: string) => void;
}

export interface SelectWidgetProps extends Components.MmSelectModal {
