    logger(
      `[StorageManagerWeb: persistAccounts()] enabled=${this.enabled}`,
      accounts,
    );

    const payload = JSON.stringify(accounts);
    localStorage.setItem(STORAGE_DAPP_SELECTED_ADDRESS, payload);
  }

